void initMasks(mask * mval, uint8_t * L, uint8_t * L2)
{
	int i;
	for(i=0; i<n; i++)
	{
		mval->L[i] = L[i];
		mval->R[i] = 0;
	}
	for(i=0; i<2*n; i++)
		mval->L2[i] = L2[i];
}

void copyMasks(mask * mout, mask * min)
{
	int i;
	for(i=0; i<n; i++)
		mout->R[i] = min->R[i];
}

void encodeByte(mask * mval, uint8_t val)
{
	int i;
	uint8_t tmp;
	
	// Random bytes from R[1] to R[n]
	for(i=1; i<n; i++)
		genRandByte(&(mval->R[i]));
	
	// Compute R[0]
	mval->R[0] = 0;
	for(i=1; i<n; i++)
	{
		gf256Multiply(&tmp, mval->L[i], mval->R[i]);
		mval->R[0] ^= tmp;
	}
	mval->R[0] ^= val;
}

void decodeByte(uint8_t * val, mask * mval)
{
	int i;
	uint8_t tmp;
	
	*val = 0;
	for(i=0; i<n; i++)
	{
		gf256Multiply(&tmp, mval->L[i], mval->R[i]);
		*val ^= tmp;
	}
}

void refreshMask(mask * mout, mask * min)
{
	int i; uint8_t A[n];
	uint8_t tmp, val;

	for(i=0; i<n; i++)
		mout->L[i] = min->L[i];

	// Compute orthogonal vector A
	val = 0;
	for(i=1; i<n; i++)
	{
		genRandByte(&A[i]);
		gf256Multiply(&tmp, A[i], min->L[i]);
		val = val ^ tmp;
	}
	A[0] = val;	// because L1^{-1} = 1	
	
	// Add A to R
	for(i=0; i<n; i++)
	{
		mout->R[i] = min->R[i] ^ A[i];
	}
}

void maskSquaring(mask * out, mask * op)
{
	int i;
	uint8_t tmp;
	
	for(i=0; i<n; i++)
	{
		out->L[i] = op->L[i];
		gf256Multiply(&(out->R[i]), op->L[i], tab2[op->R[i]]);
	}
}

void maskSquaring2(mask * out, mask * op)
{
	int i;
	uint8_t tmp,tmp2;
	
	for(i=0; i<n; i++)
	{
		out->L[i] = op->L[i];
		tmp = tab4[op->L[i]]; 
		gf256Inverse(&tmp2,op->L[i]);
		gf256Multiply(&tmp, tmp, tmp2);
		gf256Multiply(&(out->R[i]), tmp, tab4[op->R[i]]);
	}
}

void maskSquaring4(mask * out, mask * op)
{
	int i;
	uint8_t tmp,tmp2;
	
	for(i=0; i<n; i++)
	{
		out->L[i] = op->L[i];
		tmp = tab16[op->L[i]]; 
		gf256Inverse(&tmp2,op->L[i]);
		gf256Multiply(&tmp, tmp, tmp2);
		gf256Multiply(&(out->R[i]), tmp, tab16[op->R[i]]);
	}
}

void maskAddition(mask * out, mask * op1, mask * op2)
{
	int i;
	for(i=0; i<n; i++)
	{
		out->L[i] = op1->L[i];
		out->R[i] = op1->R[i] ^ op2->R[i];
	}
}

// IPMult(1)
// ------------------------------------------------
void maskMultiplication(mask * out, mask * op1, mask * op2)
{
	int i,j;
	uint8_t T[n][n], U[n][n], Up[n][n], V[n][n];
	
	uint8_t tmp, val;
	
	// 1) Compute product matrix T
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			gf256Multiply(&(T[i][j]), op1->R[i], op2->R[j]);
			gf256Multiply(&(T[i][j]), T[i][j], op1->L[j]);
		}
	}
/*	
	printf("\nT: \n");
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			printf("0x%02x ",T[i][j]);
		}
		printf("\n");
	}
*/
	
	// 2) Compute matrices U, Up
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			if(i==j) Up[i][j] = 0;
			if(i<j)	genRandByte(&Up[i][j]);
			if(i>j)	Up[i][j] = Up[j][i];
			
			gf256Inverse(&tmp, op1->L[i]);
			gf256Multiply(&U[i][j],Up[i][j],tmp); 
		}
	}
/*	
	printf("\nUp: \n");
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			printf("0x%02x ",Up[i][j]);
		}
		printf("\n");
	}
	
	printf("\nU: \n");
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			printf("0x%02x ",U[i][j]);
		}
		printf("\n");
	}
*/
	
	// 3) Compute matrix V
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
			V[i][j] = U[i][j] ^ T[i][j]; 
	}
	
/*	
	printf("\nV: \n");
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			printf("0x%02x ",V[i][j]);
		}
		printf("\n");
	} 
*/
		
	// 4) Compute output
	for(i=0; i<n; i++)
	{
		out->L[i] = op1->L[i];
		out->R[i] = 0;
		for(j=0; j<n; j++)
		{
			out->R[i] = out->R[i] ^ V[i][j];
		}
	}
	
}

// IPMult(2)
// ------------------------------------------------
void maskMultiplication2(mask * out, mask * op1, mask * op2)
{
	int i,j;
	uint8_t u[n], Ap[n][n], Bp[n], T[n][n], U[n][n], Up[n][n], V[n][n];
	
	uint8_t tmp, val;
	
	// 1) Compute vector u
	for(i=0; i<n; i++)
		genRandByte(&u[i]);
		
/*	
	printf("\nu: \n");
	for(i=0; i<n; i++)
	{
		printf("0x%02x ",u[i]);
	}
*/
		
		
	// 2) Compute matrix Ap
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			if(i!=j)	Ap[i][j] = op1->R[i] ^ u[j];
			else		Ap[i][j] = op1->R[i];
		}
	}	

/*	
	printf("\n");
	printf("\nAp: \n");
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			printf("0x%02x ",Ap[i][j]);
		}
		printf("\n");
	}
*/

	// 3) Compute vector Bp
	for(i=0; i<n; i++)
	{
		gf256Multiply(&(Bp[i]), op2->R[i], u[i]);
		gf256Multiply(&(Bp[i]), Bp[i], op1->L[i]);
	}

/*		
	printf("\nBp: \n");
	for(i=0; i<n; i++)
	{
		printf("0x%02x ",Bp[i]);
	}
	printf("\n");
*/
	
	// 4) Compute product matrix T
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			gf256Multiply(&(T[i][j]), Ap[i][j], op2->R[j]);
			gf256Multiply(&(T[i][j]), T[i][j], op1->L[j]);
		}
	}
	
/*		printf("\nT: \n");
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			printf("0x%02x ",T[i][j]);
		}
		printf("\n");
	}
*/
	
	// 5) Compute matrices U, Up
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			if(i==j) Up[i][j] = 0;
			if(i<j)	genRandByte(&Up[i][j]);
			if(i>j)	Up[i][j] = Up[j][i];
			
			gf256Inverse(&tmp, op1->L[i]);
			gf256Multiply(&U[i][j],Up[i][j],tmp); 
		}
	}
/*		
	printf("\nUp: \n");
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			printf("0x%02x ",Up[i][j]);
		}
		printf("\n");
	}

	printf("\nU: \n");
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			printf("0x%02x ",U[i][j]);
		}
		printf("\n");
	}
*/
	
	// 6) Compute matrix V
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			V[i][j] = U[i][j] ^ T[i][j]; 
			if(i!=j)		V[i][j] = V[i][j] ^ Bp[j];
		}
	}
	
/*	
	printf("\nV: \n");
	for(i=0; i<n; i++)
	{
		for(j=0; j<n; j++)
		{
			printf("0x%02x ",V[i][j]);
		}
		printf("\n");
	} 
*/
		
	// 7) Compute output
	for(i=0; i<n; i++)
	{
		out->L[i] = op1->L[i];
		out->R[i] = 0;
		for(j=0; j<n; j++)
		{
			out->R[i] = out->R[i] ^ V[i][j];
		}
	}
	
}


void maskMultConst(mask * out, mask * op1, uint8_t op2)
{
	int i;
	
	for(i=0; i<n; i++)
	{
		gf256Multiply(&(out->R[i]),op1->R[i],op2);
		out->L[i] = op1->L[i];
	}

	//refreshMask(out, out);
}

void maskAddConst(mask * mval, mask * op1, uint8_t val)
{
	int i, flag;
	uint8_t tmp, tmp2;
	
	mval->L[0] = op1->L[0];	
	for(i=0; i<n; i++)
	{
		mval->L[i] = op1->L[i];
		mval->R[i] = op1->R[i];
	}
	
	gf256Inverse(&tmp, op1->L[0]);
	gf256Multiply(&tmp2, tmp, val);
	mval->R[0] = op1->R[0] ^ tmp2;
}